import React, { useEffect, useRef, useState } from 'react';
import '../styles/canvas.scss';
import { observer } from 'mobx-react-lite';
import canvasState from '../store/canvasState';
import toolState from '../store/toolState';
import Brush from '../tools/Brush';
import { useParams } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';

const Canvas: React.FC = observer(() => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const usernameRef = useRef<HTMLInputElement>(null);
  const [modal, setModal] = useState(true);
  const params = useParams();

  useEffect(() => {
    canvasState.setCanvas(canvasRef.current);
    toolState.setTool(new Brush(canvasRef.current as HTMLCanvasElement));
  }, []);

  useEffect(() => {
    if (canvasState.username) {
      const socket = new WebSocket('ws://localhost:5000/');
      canvasState.setSocket(socket);
      canvasState.setSessionId(params.id as string);
      socket.onopen = () => {
        socket.send(
          JSON.stringify({
            id: params.id,
            username: canvasState.username,
            method: 'connection',
          })
        );
      };
      socket.onmessage = (event) => {
        let msg = JSON.parse(event.data);
        switch (msg.method) {
          case 'connection':
            console.log(`User ${msg.username} connected`);
            break;

          case 'draw':
            drawHandler(msg);
            break;
        }
      };
    }
  }, [canvasState.username]);

  const mouseDownHandler = () => {
    canvasState.pushToUndo(
      (canvasRef.current as HTMLCanvasElement).toDataURL()
    );
  };

  const connectHandler = () => {
    canvasState.setUsername((usernameRef.current as HTMLInputElement).value);
    setModal(false);
  };

  return (
    <>
      <div className="canvas">
        <Modal show={modal} onHide={() => setModal(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Your name</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Control ref={usernameRef} />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={() => connectHandler()}>
              Connect
            </Button>
          </Modal.Footer>
        </Modal>
        <canvas
          ref={canvasRef}
          width={600}
          height={400}
          onMouseDown={mouseDownHandler}
        ></canvas>
      </div>
    </>
  );
});

export default Canvas;
